  <form class="formulario">
      <a routerLink="/home" class="logo">
          <img>
      </a>
      <mat-form-field>
          <input  matInput placeholder="E-mail:"  name="email">
      </mat-form-field>
      <mat-form-field>
          <input  type='password' matInput placeholder="Senha:"  name="password">
      </mat-form-field>

      <button mat-raised-button type="submit" (click)="login()">
