import { DialogRef } from '@angular/cdk/dialog';
import { Component } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { EventsService } from 'src/app/services/events.service';

@Component({
  selector: 'app-event-create',
  templateUrl: './event-create.component.html',
  styleUrls: ['./event-create.component.css']
})
export class EventCreateComponent {
  empForm: FormGroup;


  Categoria: string[]=
  [
    'Rock',
    'Pop',
    'Eletrônica',
    'Metalcore',
    'RAP',
    'Sertanejo',
    'Forró'
  ];

  constructor(private _dialog: MatDialogprivate, fb: FormBuilder, private _eventService:EventsService, private _dialogRef: DialogRef <EventCreateComponent>) {
    this.empForm = this.fb.group({
      id: '', 
      name:'',
      date:'',
      custo:'',
      localizacao:'',
      descricao:'',
      categoria:'',
      cidade: '',
      contato: '',
      imagem:'' 
    })   
  }


  openEventCreateForm() {
    this._dialog.open(EventCreateComponent)
  }


  // onFileSelected(event:any){
  //   const file: File = event.target.files[0]
  //   this.empForm.patchValue({image: file})
  // }

  public GetFileOnLoad(event: any) {
    var file = event.target.files[0];
    var element = document.getElementById("fakeFileInput") as HTMLInputElement | null;
    if(element != null) {
      element.value = file?.name;
    }

  }

  onFormSubmit() {
    if(this.empForm.valid){
      this._eventService.createevent(this.empForm.value).subscribe({
        next:(val: any) => {
          alert('Evento criado com sucesso!')
          this._dialogRef.close();
        },
        error:(err) =>{
          console.error(err)
        }
      })
    }
  }

}
