import { Component, OnInit  } from '@angular/core';
import { MatDialog } from '@angular/material/dialog';
import { Router } from '@angular/router';
import { FormBuilder, FormGroup, Validators, FormsModule } from '@angular/forms';
import { IUsuario } from '../../models/usuario'
import { MatSnackBar } from '@angular/material/snack-bar';
import { ILogin } from '../../models/login.interface';
import { LoginService } from './login.service';



@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})

export class LoginComponent implements OnInit {


  constructor(private loginService: LoginService, private router: Router) {}
  credentials: IUsuario = {
    id: '',
    email: '',
    senha: '',
  };

  token: ILogin = {
    user: {
      _id: 0,
      username:'',
      password:'',
      ativo: false,
      status_adm: false,
    },
    token: '',
  };


  ngOnInit(): void {}

  login(){
    this.loginService.submit(this.credentials);
    this.router.navigate(['/home']);
  }
}


<div class="texto">
    <h2><b>Bem-Vindo!</b></h2>
</div>
<div class="sub-texto">
    <h3>Realize o login para ter acesso ao gerenciamento de seus eventos!</h3>
</div>
<div class="login-wrapper">
  <form class="formulario">
      <mat-form-field>
          <input  [(ngModel)]="credentials.email" matInput placeholder="E-mail:"  name="email">
      </mat-form-field>
      <mat-form-field>
          <input [(ngModel)]="credentials.senha"  type='password' matInput placeholder="Senha:"  name="password">
      </mat-form-field>
      <button mat-raised-button type="submit" (click)="login()">
       Login
      </button>
  </form>
</div>
