import { Component, OnInit  } from '@angular/core';
import { MatDialog } from '@angular/material/dialog';
import { Router } from '@angular/router';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { IUsuario } from '../../models/usuario'
import { MatSnackBar } from '@angular/material/snack-bar';
import { UsuarioService } from '../../services/usuario.service'


@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {

  formLogin!: FormGroup
  constructor(private formBuilder: FormBuilder,
              private usuarioService: UsuarioService,
              private snackBar: MatSnackBar) { }

              ngOnInit(): void {
    this.criarForm();
  }
  criarForm(): void{
    this.formLogin = this.formBuilder.group({
      email: ['', [Validators.required, Validators.email]],
      senha: ['', [Validators.required]]
    });
  }
  logar(){
    if(this.formLogin.invalid) return;
    var usuario = this.formLogin.getRawValue() as IUsuario;
    this.usuarioService.logar(usuario).subscribe((response) => {
        if(!response.sucesso){
          this.snackBar.open('Falha na autenticação', 'Usuário ou senha incorretos.', {
            duration: 3000
          });
        }
    })
  }
}
<div class="login-wrapper">
  <form class="formulario">
      <a routerLink="/home" class="logo">
          <img src="../../../../assets/img/AVAGAMES-logo.png" alt="Logo da Avagames">
      </a>
      <mat-form-field>
          <input matInput placeholder="E-mail:" [(ngModel)]="credentials.email" name="email">
      </mat-form-field>
      <mat-form-field>
          <input type='password' matInput placeholder="Senha:" [(ngModel)]="credentials.senha" name="senha">
      </mat-form-field>

      <button mat-raised-button type="submit" (click)="login()">
    Login
  </button>
      <nav>
          <ul>
              <li>
                  <p>Ainda não tem conta?</p>
              </li>
              <li><a routerLink="/cadastroUsuario">Cadastre-se</a></li>
          </ul>
      </nav>
  </form>
</div>
