import { Component, OnInit  } from '@angular/core';
import { MatDialog } from '@angular/material/dialog';
import { Router } from '@angular/router';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { IUsuario } from '../../models/usuario'
import { MatSnackBar } from '@angular/material/snack-bar';
import { ILogin } from '../../models/login.interface';
import { LoginService } from './login.service';


@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})

export class LoginComponent implements OnInit {
  constructor(private loginService: LoginService, private router: Router) {}
  credentials: IUsuario = {
    id: '',
    email: '',
    senha: '',
  };

  token: ILogin = {
      username: '',
      password:''
    };


  ngOnInit(): void {}

  login(): void {
    this.loginService.submit(this.credentials).subscribe((token) => {
      if (token) {
        (this.token = token),
          sessionStorage.setItem('token', JSON.stringify(token)),
          this.router.navigate(['/home']);
      } else {
        this.router.navigate(['/login']);
      }
    });

    this.loginService.submit(this.credentials);
  }
}


<div class="login-wrapper">
  <form class="formulario">
      <a routerLink="/home" class="logo">
          <img>
      </a>
      <mat-form-field>
          <input matInput placeholder="E-mail:" [(ngModel)]="credentials.email" name="email">
      </mat-form-field>
      <mat-form-field>
          <input type='password' matInput placeholder="Senha:" [(ngModel)]="credentials.senha" name="senha">
      </mat-form-field>

      <button mat-raised-button type="submit" (click)="login()">
    Login
  </button>
      <nav>
          <ul>
              <li>
                  <p>Ainda não tem conta?</p>
              </li>
              <li><a routerLink="/cadastroUsuario">Cadastre-se</a></li>
          </ul>
      </nav>
  </form>
</div>
