import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Router } from '@angular/router';
import { Observable } from 'rxjs';
import { IUsuario } from '../../models/usuario'
import { ILogin } from '../../models/login.interface';

@Injectable({
  providedIn: 'root',
})
export class LoginService {
  constructor(private http: HttpClient, private router: Router, usuarioAutenticado: boolean = false, mostrarMenuEmitter = new EventEmitter<boolean>();) {}

  // submit(credentials: IUsuario): Observable<ILogin>{
  //   // return this.http.post<ILogin>('http://localhost:3000/api/user', credentials);

  submit(credentials: IUsuario){

    if (credentials.email === 'admin' && 
        credentials.senha === 'admin') {

      this.usuarioAutenticado = true;

      this.mostrarMenuEmitter.emit(true);

      this.router.navigate(['/']);

    } else {
      this.usuarioAutenticado = false;

      this.mostrarMenuEmitter.emit(false);
    
  }

}

logout(): void{
  sessionStorage.removeItem('token');
  this.router.navigate(['/login']);
}

}
